import React from 'react';
import Cookie from '../common/Cookie'
import languages from '../languages.json';

/**
 * CHECK LANGUAGE - START
 * This will check if there are already a language change made by the
 * user. If there are none, it will get the browser language to set
 * the content.
 */
let lang = navigator.language.slice(0, 2),
    langCookie = Cookie.getCookie('dmAppLanguage');

if (langCookie)
    lang = langCookie;

const LanguageContext = React.createContext(lang);
/**
 * CHECK LANGUAGE - END
 */

class LanguageContextProvider extends React.Component {

    render() {
        return(
            <LanguageContext.Provider value={true}>
                {this.props.children}
            </LanguageContext.Provider>
        );
    }
}

export default LanguageContextProvider;
