import React from 'react';
import { LanguageContext } from '../../context/LanguageContext';

const OtherLanguages = (props) => {
    const othersLang = props.langs.map((language, index) => 
        <a href={language.href} key={index}>
            <span className="flag-icon">{language.flag}</span>
            {language.text}
        </a>
    );

    return(
        <div className="language-selection-box">
            {othersLang}
        </div>
    );
}

class NavigationLinks extends React.Component {

    static contextType = LanguageContext;

    render() {
        let navItems = this.context.header.navbar,
            langSelection = navItems.langSelection;

        return (
            <div className="nav-links">
                <ul>
                    <li>
                        <a className="nav-item" href="#about">{navItems.about}</a>
                    </li>
                    <li>
                        <a className="nav-item" href="#wallet">{navItems.wallet}</a>
                    </li>
                    <li>
                        <a className="nav-item" href="#whitepaper">{navItems.whitepaper}</a>
                    </li>
                    <li>
                        <a className="nav-item" href="#roadmap">{navItems.roadmap}</a>
                    </li>
                    <li>
                        <a className="nav-item" href="#financing">{navItems.financing}</a>
                    </li>
                    <li>
                        <a className="nav-item" href="#faq">{navItems.faq}</a>
                    </li>

                    <li>
                        <a className="nav-item" href="/">
                            <span className="flag-icon">{langSelection.active.flag}</span>
                            {langSelection.active.text}
                        </a>

                        <OtherLanguages langs={langSelection.hidden} />
                    </li>
                </ul>
            </div>
        );
    }
}

export default NavigationLinks;
