import React from 'react';
import './Header.css';
import logo from './images/logo-dm.png';
import {LanguageContext} from '../../context/LanguageContext';

const LanguageSelector = () => {

    return (
        <li>
            <a href="/">
                <span className="flag-icon"></span>
            </a>

            <div className="">
                <a href="#en">
                    <span className="flag-icon"></span>English
                </a>
                <a href="#es">
                    <span className="flag-icon"></span>Espa&ntilde;ol
                </a>
                <a href="#pt">
                    <span className="flag-icon"></span>Portugu&ecirc;s
                </a>
            </div>
        </li>
    );
}

const PageLogo = () => {

    return (
        <a href="/">
            <img src={logo} alt="Logo" />
        </a>
    );
}

class Header extends React.Component {

    render() {
        return (
            <header>
                <div className="container">
                    <PageLogo />
                    <button className="burgerBtn">&equiv;</button>
                    <NavigationBar />
                </div>
            </header>
        );
    }
}

export default Header;
