import React from 'react';
import Cookie from '../common/Cookie'

Cookie.setCookie('dmAppLanguage', 'en', 7);

let languagesData = require('../languages.json'), //Get languages data from local JSON file
    lang = navigator.language.slice(0, 2), //Set browser language as default
    langCookie = Cookie.getCookie('dmAppLanguage'); //Get cookie data.

//If there is a cookie with the preferred language
if (langCookie)
    lang = langCookie;

export const LanguageContext = React.createContext();

class LanguageContextProvider extends React.Component {

    render() {
        return(
            <LanguageContext.Provider value={languagesData[lang]}>
                {this.props.children}
            </LanguageContext.Provider>
        );
    }
}

export default LanguageContextProvider;
