import React from 'react';

import { LanguageContext } from '../../context/LanguageContext';
import './About.css';
import image from './images/about-ico.png';

class About extends React.Component {

    static contextType = LanguageContext;

    render() {

        let aboutData = this.context.about;

        return(
            <section id="about">
                <div className="container">
                    <div className="content-title">
                        <h2>{aboutData.title}</h2>

                        <div className="title-bars">
                            <div className="long-bar"></div>
                            <div className="medium-bar"></div>
                            <div className="short-bar"></div>
                        </div>
                    </div>

                    <div className="content">
                        <div className="content-data">

                        </div>

                        <div className="content-image">
                            <a href="#!" className="video-btn" data-toggle="modal" data-src="https://player.vimeo.com/video/310644541" data-target="#ico-modal">
                                <img className="img-fluid" src={image} alt="What is Crypto?"/>
                            </a>


                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default About;
