import React from 'react';
import Cookie from '../components/common/Cookie';

let languagesData,
    language,
    path = '', //Json file location
    langCookie = Cookie.getCookie('dmAppLanguage'); //Get cookie data.

//If there is a cookie with the preferred language
if (langCookie)
    language = langCookie;

switch (language) {
    case "en":
        path = '../assets/data/en.lang.json';
        break;

    case "es":
        path = '../assets/data/es.lang.json';
        break;

    case "pt":
        path = '../assets/data/pt.lang.json';
        break;

    default:
        console.log("entrou aqui");
        language = navigator.language.slice(0, 2);
        path = './assets/data/' + browserLang + '.lang.json';

        break;
}

languagesData = require(path);

//Update HTML language attribute
document.querySelector('html').setAttribute('lang', language);

export const LanguageContext = React.createContext();

class LanguageContextProvider extends React.Component {

    render() {
        return(
            <LanguageContext.Provider value={languagesData}>
                {this.props.children}
            </LanguageContext.Provider>
        );
    }
}

export default LanguageContextProvider;
