import React from 'react';
import { LanguageContext } from '../../context/LanguageContext';

const OtherLanguages = (langs) => {
    console.log(typeof langs);

    // const others = langs.map((lang) => 
    //     <a href={lang.href}>
    //         <span className="flag-icon">{lang.flag}</span>
    //         {lang.text}
    //     </a>
    // );

    const othersLang = null;

    for(let lang in langs) {
        othersLang +=   <a href={lang.href}>
                            <span className="flag-icon">{lang.flag}</span>
                            {lang.text}
                        </a>
    }

    return(
        <div className="">
            {othersLang}
        </div>
    );

    // <a href={}>
    //     <span className="flag-icon"></span>English
    //                         </a>
    //     <a href="#es">
    //         <span className="flag-icon"></span>Espa&ntilde;ol
    //                         </a>
}

class NavigationBar extends React.Component {

    static contextType = LanguageContext;

    render() {
        let navItems = this.context.header.navbar,
            langSelection = navItems.langSelection;

            // console.log(typeof langSelection.hidden);
            // console.log(langSelection.hidden);

        return (
            <nav>
                <ul>
                    <li>
                        <a className="navItem" href="#about">{navItems.about}</a>
                    </li>
                    <li>
                        <a className="navItem" href="#wallet">{navItems.wallet}</a>
                    </li>
                    <li>
                        <a className="navItem" href="#whitepaper">{navItems.whitepaper}</a>
                    </li>
                    <li>
                        <a className="navItem" href="#roadmap">{navItems.roadmap}</a>
                    </li>
                    <li>
                        <a className="navItem" href="#financing">{navItems.financing}</a>
                    </li>
                    <li>
                        <a className="navItem" href="#faq">{navItems.faq}</a>
                    </li>

                    <li>
                        <a href="/">
                            <span className="flag-icon">{langSelection.active.flag}</span>
                            {langSelection.active.text}
                        </a>

                        <OtherLanguages lang={langSelection.hidden} />
                    </li>
                </ul>
            </nav>
        );
    }
}

export default NavigationBar;
