                    <li>
                        <a href="/">
                            <span className="flag-icon">{langSelection.active.flag}</span>
                            {langSelection.active.text}
                        </a>

                        <OtherLanguages lang={langSelection.hidden} />
                    </li>
                </ul>
            </nav>
        );
    }
}

export default NavigationBar;
